var service_id="smtp_server";function sendInvite(e,i){emailjs.send(service_id,"invite_pollify",{email_to:e,username:i})}function sendFriendRequest(e,i){emailjs.send(service_id,"friend_request",{email_to:e,username:i})}function sendActivition(e,i,n){emailjs.send(service_id,"activate_account",{email_to:e,username:i,guid:n})}function sendAcceptFriend(e,i){emailjs.send(service_id,"friend_and_poll",{email_to:e,title:"New friend!",username:i,text1:"accepted your friend request",text2:"and start new polls with your new friend"})}function sendPollInvite(e,i,n){emailjs.send(service_id,"friend_and_poll",{email_to:e,title:"Poll Invite!",username:i,text1:"invited you to a new poll: ("+n+")",text2:"to answer this invite"})}function resetPassword(e,i,n){emailjs.send(service_id,"forgot_password",{email_to:e,username:i,guid:n})}